jQuery.fn.extend({tab:function(d){return this.each(function(){var n=this,t=$("<div></div>").addClass("tab-container").append("<div class='tab-header'></div><div class='tab-content'></div>");$.each(d,function(d,n){t.find(".tab-header").append("<div>"+n.title+"</div>").end().find(".tab-content").append("<div>"+n.content+"</div>")}),t.find(".tab-header div:eq(0),.tab-content div:eq(0)").addClass("current"),t.appendTo(n)})}}),$("div").tab();